import styled from "styled-components";
import { motion, useScroll, useTransform } from "framer-motion";
import { Link, useMatch, useNavigate } from "react-router-dom";
import { useState } from "react";
import { useForm } from "react-hook-form";

const logoVariants = {
    normal: {
        fillOpacity: 1,
        stroke: "none"
    },
    active: {
        fillOpacity: [1,0,1],
        transition: {
            repeat: Infinity
        },
        stroke:"white",
        strokeWidth:"10"
    }
}

export interface IForm {
    keyword: string;
}

const Header = () => {

 
    return (
        <Nav>


        </Nav>
    )
}

export default Header;

const Nav = styled(motion.div)`
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    position: fixed;
    top: 0;
    background-color: black;
    height: 80px;
    font-size: 14px;
    padding: 20px 60px;
    z-index: 1000000000;
`;

const Col = styled.div`
    display: flex;
    align-items: center;
`;

const Logo = styled(motion.svg)`
    margin-right: 50px;
    width: 95px;
    height: 25px;

`;

const Items = styled.ul`
    display: flex;
    align-items: center;
    position: relative;
`;

const Item = styled.li`
    margin-right: 20px;
    color: black;
    transition: color 0.3s ease-in-out;
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
    &:hover {
        color: green;
    }
`;

const Search = styled.form`
    color: white;
    svg {
        height: 25px;
    };
    display: flex;
    align-items: center;
    position: relative;
`;

const Circle = styled(motion.span)`
    position: absolute;
    width: 5px;
    height: 5px;
    background-color: blue;
    bottom: -10px;
    margin: 0 auto;
    border-radius: 5px;
`;

const Input = styled(motion.input)`
    transform-origin: right center;
    position: absolute;
    right: 0;
    padding: 5px 10px;
    padding-left: 40px;
    z-index: -1;
    color: white;
    background-color: transparent;
    border: 1px solid blue;
`;
