    return (


            <Wrapper>
                                        <ReactPlayer
                                            url={`https://www.youtube.com/embed/VB0JPu6NUyI?showinfo=0&enablejsapi=1&origin=http://localhost:3000`}
                                            // https://www.youtube.com/embed/${clickedTvVideo?.results[0]?.key}?showinfo=0&enablejsapi=1&origin=http://localhost:3000
                                            
                                            width={'500px'}
                                            height={'300px'}
                                            volume={0.3}
                                            start={0}
                                            playing={true}
                                            loop={true}
                                            style={{pointerEvents: 'none'}}
                                        >
                                        </ReactPlayer>

            </Wrapper>

        
    );
}

export default Home;

export const Wrapper = styled.div`
    background: white;
    height: 2000px;
`;




