import riotLogoWhite from '../assets/riot-games-log-white.png';
import TFTLogo from '../assets/TFTLogo.png';
import { Link } from "react-router-dom";
import styled from 'styled-components';
import { motion} from "framer-motion";





const Footer = () => {


  return (
<footer style={{backgroundColor: 'black', width: '100%', height: '500px', display: 'flex', flexDirection: 'column', alignItems:'center'}}>
    <div>
        <Logo src={riotLogoWhite} />

    </div>

    <div>
        <p style={{maxWidth:'800px', color: 'gray'}}>
        © 2022 Riot Games, Inc. League of Legends, Teamfight Tactics and all related logos, characters, names and distinctive likenesses thereof are the exclusive property of Riot Games, Inc. All rights reserved.

        </p>
        <div>
        <p style={{maxWidth:'600px', color: 'gray', marginTop: '40px'}}>
            라이엇게임즈코리아 유한회사 서울특별시 강남구 테헤란로 521 30층(삼성동, 파르나스타워)
            <br />
            대표자: 조혁진 | 대표전화: 02-3454- 1560 | FAX: 02-3454-1565
            <br />
            사업자등록번호: 120-87-68488|통신판매업신고: 2011-서울강남-0218

       

        </p>
        
            
        </div>
    </div>

    <div>
    <Items>

                    <Item whileHover={{backgroundColor: '#495579', borderBottom: '2px solid orange'}}>새소식</Item>
                                        

                    <Item whileHover={{backgroundColor: '#495579', borderBottom: '2px solid orange'}}>세트 개요</Item>

                        <Item whileHover={{backgroundColor: '#495579', borderBottom: '2px solid orange'}}>연구소</Item>

      
                        <Item whileHover={{backgroundColor: '#495579', borderBottom: '2px solid orange'}}>날먹덱</Item>
            

                </Items>
    </div>




</footer>
    

  );
};

export default Footer;

const Nav = styled(motion.div)`
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    position: fixed;
    top: 10px;
    background: black;
    height: 80px;
    font-size: 14px;
    padding: 20px 60px;
    z-index: 1000000000;
    border-radius: 10px;
`;

const Col = styled.div`
    display: flex;
    align-items: center;
`;

export const Logo = styled(motion.img)`

    height: 200px;

    &:hover {
        filter: brightness(170%);
        scale: 1.2;
    }

`;

const Items = styled.ul`
    display: flex;
    align-items: center;
    position: relative;

`;

const Item = styled(motion.li)`
    margin-right: 20px;
    color: white;
    border-radius: 15px;
    white-space: nowrap;
    font-size: 18px;
    padding: 15px;
    font-weight: 600;
    transition: color 0.3s ease-in-out;
    display: flex;
    justify-content: center;
    flex-direction: row;
    align-items: center;


`;
