import { FaArrowDown } from 'react-icons/fa';
import { Link } from "react-router-dom";
import styled from 'styled-components';
import { motion, useScroll, useTransform } from "framer-motion";


const logoVariants = {
    normal: {
        fillOpacity: 1,
        stroke: "none"
    },
    active: {
        fillOpacity: [1,0,1],
        transition: {
            repeat: Infinity
        },
        stroke:"white",
        strokeWidth:"10"
    }
}

const Header = () => {


  return (
    <Nav>
        <Col>
                <Logo>
                
                </Logo>
                <Items>
                    <Link to="/">
                    <Item whileHover={{backgroundColor: '#495579', borderBottom: '2px solid orange'}}>디아블로</Item>
                    </Link>
                    
                    <Link to="/tv">
                        <Item whileHover={{backgroundColor: '#495579'}}>디아블로</Item>
                    </Link>
                    <Link to="/search">
                        <Item whileHover={{backgroundColor: '#495579'}}>오버워치</Item>
                    </Link>
                    <Link to="/search">
                        <Item whileHover={{backgroundColor: '#495579'}}>스타크래프트</Item>
                    </Link>

                </Items>
            </Col>

    </Nav>

  );
};

export default Header;

const Nav = styled(motion.div)`
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    position: fixed;
    top: 10px;
    background: black;
    height: 80px;
    font-size: 14px;
    padding: 20px 60px;
    z-index: 1000000000;
    border-radius: 10px;
`;

const Col = styled.div`
    display: flex;
    align-items: center;
`;

const Logo = styled(motion.svg)`
    margin-right: 50px;
    width: 95px;
    height: 25px;
    fill: red;
`;

const Items = styled.ul`
    display: flex;
    align-items: center;
    position: relative;

`;

const Item = styled(motion.li)`
    margin-right: 20px;
    color: white;
    border-radius: 15px;
    white-space: nowrap;
    font-size: 18px;
    padding: 15px;
    font-weight: 600;
    transition: color 0.3s ease-in-out;
    display: flex;
    justify-content: center;
    flex-direction: row;
    align-items: center;

`;
