import { motion } from "framer-motion";
import React from "react";
import ReactPlayer from "react-player";
import styled from "styled-components";
import YouTubeEmbed from "../YouTubeEmbed";


const Home = () => {
    
    

    return (


            <SectionOne>

                <VideoBoxLine>
                <div>TEAMFIGHT TACTICS</div>
    <ReactPlayer
      url="https://www.youtube.com/embed/4HPfUrO5fBY&t=1s?showinfo=0&enablejsapi=1&origin=http://localhost:3000"
      playing={true}
      width="600px"
      height="300px"
      volume={0.3}
      loop={true}
    
      style={{pointerEvents: 'none', borderRadius:'20%', zIndex: '1',overflow: 'visible'}}
    />
    <PlayBtn>무료로 플래이하기</PlayBtn> 


    </VideoBoxLine>


            </SectionOne>

        
    );
}

export default Home;

export const SectionOne = styled.div`
    background: linear-gradient(to right bottom, #fedd49, #fe5e49);
    height: 2000px;
    display: flex;
    justify-content: center;

`;

export const VideoBoxLine = styled.div`
    background: none;
    margin-top:200px;
    height: 600px;
    width: 500px;
    border: 4px solid white;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    font-size: 50px;
    font-weight: 1000;
    z-index: 4;
`;

export const PlayBtn = styled.button`
    border: 2px solid #07afd9;
    backgroun-color: transparent;
    border-radius: 8px;
    width: 180px;
    height: 60px;
    margin-top: 50px;
    font-weight: 800;
    box-shadow: 0 0 22px #07c7d9;
    color: #07afd9;

    


`;




